mapper.select:
select
expense_id, cause, em_id, create_time, end_time, next_deal_em, total_amount, status
from expense_report


        and expense_id = #{expenseId}


        and cause = #{cause}


        and em_id = #{emId}


        and create_time = #{createTime}


        and end_time = #{endTime}


        and next_deal_em = #{nextDealEm}


        and total_amount = #{totalAmount}


        and status = #{status}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from expense_report


        and expense_id = #{expenseId}


        and cause = #{cause}


        and em_id = #{emId}


        and create_time = #{createTime}


        and end_time = #{endTime}


        and next_deal_em = #{nextDealEm}


        and total_amount = #{totalAmount}


        and status = #{status}
