mapper.select:
select
expensive_detail_id, expensive_id, item, time, type, code, num, amount, comment, image
from expense_report_detail


        and expensive_detail_id = #{expensiveDetailId}


        and expensive_id = #{expensiveId}


        and item = #{item}


        and time = #{time}


        and type = #{type}


        and code = #{code}


        and num = #{num}


        and amount = #{amount}


        and comment = #{comment}


        and image = #{image}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from expense_report_detail


        and expensive_detail_id = #{expensiveDetailId}


        and expensive_id = #{expensiveId}


        and item = #{item}


        and time = #{time}


        and type = #{type}


        and code = #{code}


        and num = #{num}


        and amount = #{amount}


        and comment = #{comment}


        and image = #{image}

SELECT 1
FROM expense_report_detail
WHERE code = #{code}
  AND num = #{num}
LIMIT 1
